clear all;A1 = imread ('D:\final_project_BMOK1\futbol.jpg');A2 = rgb2hsv(A1); %HSV dönüþümü yapýldýsat=size(A2)(1);sut=size(A2)(2);A=uint8(255*A2(:,:,1)); esik=120; %120'nin altýndaki degerler siyah üstündeki deðerler beyaz yapýldýA3=0;for i=1:sat  for j=1:sut  if A(i,j)>esik     A3(i,j)=1;  else     A3(i,j)=0;  end  endendimwrite(A3,'D:\final_project_BMOK1\Hmaske.jpg');A=uint8(255*A2(:,:,2));esik=150 %150'nin altýndaki degerler siyah üstündeki deðerler beyaz yapýldýfor i=1:sat  for j=1:sut  if A(i,j)>esik     A4(i,j)=1;  else     A4(i,j)=0;  end  endendimwrite(A4,'D:\final_project_BMOK1\Smaske.jpg');Amsk=0;Amsk= (A3.*A4);Amsk = bwareaopen(Amsk,150);%150'den az sayýda yan yana gelen pikseller görmezden gelinerek karartýldý.imwrite(255*Amsk,'D:\final_project_BMOK1\_HcarpimS.jpg');A1(:,:,1)=Amsk.*A1(:,:,1);%R deðeri çarpýldýA1(:,:,2)=Amsk.*A1(:,:,2);%G deðeri çarpýldýA1(:,:,3)=Amsk.*A1(:,:,3);%B deðeri çarpýldýimwrite(A1,'D:\final_project_BMOK1\masked.jpg');%Maskelenmiþ görüntü elde edildi.